      <div class="form-container sign-up-container">
        <form onsubmit="return handleSignupForm()"  action="../models/signup.inc.php" method="post" id="signup-form">
            <h1>Create Account</h1>
            <input type="text" name="name" id="name" placeholder="Full Name">
            <input type="text" name="email" id="email" placeholder="Email">
            <input type="password" name="pwd" id="pwd" placeholder="Password">
            
            <input type="password" name="pwdrepeat" id="pwdrepeat" placeholder="Repeat Password">
            
            <button type="submit" id="submitSignup" name="submit">Sign Up</button>

            <div id="error-message" style="color: red;"></div>

        </form>

      </div>
      <div class="form-container sign-in-container">
        <form action="../models/login.inc.php" method="post">
            <h1>Sign in</h1>
            <input type="text" name="email" placeholder="Email">
            <input type="password" name="pwd" placeholder="Password">
            
            <button type="submit" name="submit" id="submitLogin">login</button>
            <p>Forgot your password ?</p>
        </form>
      </div>
      <div class="overlay-container">
        <div class="overlay">
          <div class="overlay-panel overlay-left">
            <h2>Already have an account ?</h2>
            <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit.</p>
            <button class="ghost" id="signIn">Sign In</button>
          </div>
          <div class="overlay-panel overlay-right">
            <h2>Don't have an account ?</h2>
            <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. </p>
            <button  id="signUp">sign Up</button>
          </div>
        </div>
      </div>
    </div>
  

    <script src="../assets/javascript/submit.js" ></script>
    <script>
    function handleSignupForm() {
    var name = document.getElementById('name').value;
    var email = document.getElementById('email').value;
    var pwd = document.getElementById('pwd').value;
    var pwdRepeat = document.getElementById('pwdrepeat').value;
    var errorMessage = document.getElementById('error-message');

    errorMessage.innerHTML = ''; // Efface tout message d'erreur précédent

    if (name === '' || email === '' || pwd === '' || pwdRepeat === '') {
        errorMessage.innerHTML = 'Veuillez remplir tous les champs.';

        // Construction du fragment d'URL avec les paramètres d'erreur
        var urlParams = new URLSearchParams();
        urlParams.append('error', 'input_empty');

        // Mise à jour de l'URL avec les paramètres
        history.replaceState(null, '', '?' + urlParams.toString());

        return false; // Empêche l'envoi du formulaire
    }

    return true; // Autorise l'envoi du formulaire
}

</script>


    <script src="../assets/javascript/transition-reg-log.js" ></script>

